import Layout from '../components/MyLayout.js'
import App from '../components/app.js'
import Link from 'next/link'
import List from '../components/list'
import fetch from 'isomorphic-unfetch'
import PostLink from '../components/service.js'

const Service = (props) => (
   <Layout>
    <h1>Default Todo List from the server endpoint</h1>
    <ul>
      {props.todos.map((todo) => (
        <li key={todo.id}>
          <Link as={`/p/${todo.id}`} href={`/post?id=${todo.id}`}>
            <a>{todo.name}</a>
          </Link>
        </li>
      ))}
    </ul>
    <div>
    <section>
    <div>
    <h3>My Todo List</h3>
    <ul>
      <PostLink title="Construction in progress.js" />
      <PostLink title="Project on awesome" />
      <PostLink title="Deploy apps with Zeit" />
    </ul>
  </div>
    </section>
    <section>
      <App/>
    </section>
    </div>
  </Layout>
)

Service.getInitialProps = async function() {
  const res = await fetch('http://localhost:3000/api/course')
  const data = await res.json()

  console.log(`Show data fetched. Count: ${data.length}`)

  return {
    todos: data
  }
}

export default Service;




